import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./EventDetails.css";

const EventDetails = () => {
  const { id } = useParams(); // Get the event ID from the URL
  const navigate = useNavigate();
  const [event, setEvent] = React.useState(null);
  const [showModal, setShowModal] = useState(false); // Modal visibility state

  // Mock data for teammates, friends, and waitlist
  const [teammates, setTeammates] = useState([
    { id: 1, name: "Alice", role: "Frontend Developer" },
    { id: 2, name: "Bob", role: "Backend Engineer" },
  ]);

  const [friends, setFriends] = useState([
    { id: 3, name: "Charlie", role: "Data Scientist" },
    { id: 4, name: "Diana", role: "UI/UX Designer" },
  ]);

  const [waitlist, setWaitlist] = useState([
    { id: 5, name: "Eve", role: "Project Manager" },
    { id: 6, name: "Frank", role: "DevOps Engineer" },
  ]);

  // Fetch event data when the component is mounted
  React.useEffect(() => {
    const fetchEvent = async () => {
      try {
        const response = await fetch(`/api/home/events/${id}`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setEvent(data);
      } catch (error) {
        console.error("Error fetching event:", error);
      }
    };

    fetchEvent();
  }, [id]);

  const addToTeammates = (user, fromList, setList) => {
    setTeammates((prev) => [...prev, user]); // Add to teammates
    setList((prev) => prev.filter((u) => u.id !== user.id)); // Remove from the original list
  };

  const removeFromTeammates = (user) => {
    setTeammates((prev) => prev.filter((u) => u.id !== user.id)); // Remove from teammates
    if (user.source === "friends") {
      setFriends((prev) => [...prev, user]); // Add back to friends
    } else if (user.source === "waitlist") {
      setWaitlist((prev) => [...prev, user]); // Add back to waitlist
    }
  };

  if (!event) {
    return <p>Loading event details...</p>;
  }

  return (
    <div className="event-details-container">
      <button className="back-btn" onClick={() => navigate(-1)}>← Back</button>
      <div className="event-details-card">
        <img src={event.logo} alt={`${event.company} logo`} className="event-logo" />
        <h1 className="event-title">{event.title}</h1>
        <p className="event-rate">Rate: {event.rate}</p>
        <p className="event-company">Company: {event.company}</p>
        <button className="apply-btn" onClick={() => setShowModal(true)}>Apply</button>
      </div>

      {/* Modal */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal-box">
            <button className="close-btn" onClick={() => setShowModal(false)}>✖</button>
            <div className="modal-content">
              {/* Left: Selected Teammates */}
              <div className="modal-left">
                <h3>Selected Teammates</h3>
                <ul>
                  {teammates.map((teammate) => (
                    <li key={teammate.id} className="teammate-item">
                      <strong>{teammate.name}</strong> - {teammate.role}
                      <button
                        className="remove-btn"
                        onClick={() => removeFromTeammates(teammate)}
                      >
                        -
                      </button>
                    </li>
                  ))}
                </ul>
              </div>

              {/* Right: Friends and Waitlist */}
              <div className="modal-right">
                {/* Friends List */}
                <div className="modal-friends">
                  <h3>Friends List</h3>
                  <ul>
                    {friends.map((friend) => (
                      <li key={friend.id} className="friend-item">
                        <strong>{friend.name}</strong> - {friend.role}
                        <button
                          className="add-btn"
                          onClick={() => addToTeammates({ ...friend, source: "friends" }, friends, setFriends)}
                        >
                          +
                        </button>
                      </li>
                    ))}
                  </ul>
                </div>

                {/* Waitlist */}
                <div className="modal-waitlist">
                  <h3>Waitlist</h3>
                  <ul>
                    {waitlist.map((person) => (
                      <li key={person.id} className="waitlist-item">
                        <strong>{person.name}</strong> - {person.role}
                        <button
                          className="add-btn"
                          onClick={() => addToTeammates({ ...person, source: "waitlist" }, waitlist, setWaitlist)}
                        >
                          +
                        </button>
                      </li>
                    ))}
                  </ul>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default EventDetails;
