import React, { useState } from "react";
import { useParams, useNavigate, useLocation } from "react-router-dom";
import "./EventDetails.css";

const EventDetails = () => {
  const { id } = useParams(); // Get the event ID from the URL
  const navigate = useNavigate();
  const location = useLocation();
  const { onApply } = location.state || {}; // Callback function passed from Home.js
  const [event, setEvent] = React.useState(null);
  const [showModal, setShowModal] = useState(false); // Modal visibility state
  const [applied, setApplied] = useState(false); // Track if the event is applied
  const [showPopup, setShowPopup] = useState(false); // Show "Applied!" popup

  // Fetch event data when the component is mounted
  React.useEffect(() => {
    const fetchEvent = async () => {
      try {
        const response = await fetch(`/api/home/events/${id}`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setEvent(data);
      } catch (error) {
        console.error("Error fetching event:", error);
      }
    };

    fetchEvent();
  }, [id]);

  const handleApply = () => {
    setShowPopup(true); // Show the popup
    setApplied(true); // Mark event as applied
    if (onApply) {
      onApply(); // Call the callback to update applied status in Home.js
    }
    setShowModal(false); // Close the modal
    setTimeout(() => setShowPopup(false), 2000); // Hide popup after 2 seconds
  };

  if (!event) {
    return <p>Loading event details...</p>;
  }

  return (
    <div className="event-details-container">
      <button className="back-btn" onClick={() => navigate(-1)}>‚Üê Back</button>
      <div className="event-details-card">
        <img src={event.logo} alt={`${event.company} logo`} className="event-logo" />
        <h1 className="event-title">{event.title}</h1>
        <p className="event-rate">Rate: {event.rate}</p>
        <p className="event-company">Company: {event.company}</p>
        <button className="apply-btn" onClick={() => setShowModal(true)} disabled={applied}>
          {applied ? "Applied" : "Apply"}
        </button>
      </div>

      {/* Modal */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal-box">
            <button className="close-btn" onClick={() => setShowModal(false)}>‚úñ</button>
            <div className="modal-content">
              <p>Apply for this event with your team?</p>
            </div>
            <button className="ok-btn" onClick={handleApply}>OK</button>
          </div>
        </div>
      )}

      {/* Popup */}
      {showPopup && <div className="popup">Applied!</div>}
    </div>
  );
};

export default EventDetails;
