// EventDetails.js
import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./EventDetails.css";

const EventDetails = () => {
  const { id } = useParams(); // Get the event ID from the URL
  const navigate = useNavigate();
  const [event, setEvent] = React.useState(null);

  // Fetch event data when the component is mounted
  React.useEffect(() => {
    const fetchEvent = async () => {
      try {
        const response = await fetch(`/api/home/events/${id}`); // Fetch the specific event by ID
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setEvent(data);
      } catch (error) {
        console.error("Error fetching event:", error);
      }
    };

    fetchEvent();
  }, [id]);

  if (!event) {
    return <p>Loading event details...</p>;
  }

  return (
    <div className="event-details-container">
      <button className="back-btn" onClick={() => navigate(-1)}>‚Üê Back</button>
      <div className="event-details-card">
        <img src={event.logo} alt={`${event.company} logo`} className="event-logo" />
        <h1 className="event-title">{event.title}</h1>
        <p className="event-rate">Rate: {event.rate}</p>
        <p className="event-company">Company: {event.company}</p>
        <button className="apply-btn" onClick={() => alert("Application submitted!")}>Apply</button>
      </div>
    </div>
  );
};

export default EventDetails;
